pre_install:
$batContent = @"
@echo off
setlocal EnableDelayedExpansion

set "jdk_path="

for %%J in (ORACLE_JDK_8_HOME JDK_8_HOME) do (
    if defined %%J (
        set "jdk_path=!%%J!\bin\"
    )
)

if not defined jdk_path if defined JAVA_HOME (
    set "jdk_path=%JAVA_HOME%\bin\"
)

@pushd %~dp0
@start /B "" "%jdk_path%java.exe" -jar "BlueTeam_ABC_123.jar" %*
@popd

endlocal
"@

$path = "$dir/blueteamtools.bat"
Set-Content -Path $path -Value $batContent -Encoding Ascii
